import ReactDOMStream from '../src/ReactDOMServer';
import React from 'react-universal';

function HelloMessage(props) {
  return <div className="hello">Hello {props.name}</div>;
}

class App extends React.Component {
  render() {
    return <div className="root" onClick={e => e}><HelloMessage name="world" /></div>
  }
}

// TODO: write out the html, head, and body tags
var s = ReactDOMStream.createRenderToStringStream(<App />);

s.on("end", () =>{
  // TODO: write out the rest of the page, including the closing body and html tags.
  console.log('end')
});

s.pipe(process.stdout)
